import React from "react";
import { View, Text, StyleSheet, StatusBar } from "react-native";
import PropTypes from "prop-types";
import { Fontisto } from "@expo/vector-icons";
import { LinearGradient } from "expo-linear-gradient";

const weatherOptions = {
	Thunderstorm: {
		iconName: "lightnings",
		colour: ["#232526", "#414345"],
		title: "NO WAY TO GET OUT",
		sub_title: "maybe there's some",
	},
	Drizzle: {
		iconName: "rain",
		colour: ["#1CD8D2", "#93EDC7"],
		title: "NO UMBRELLA IS REQUIRED",
		sub_title: "unless u have no d",
	},
	Rain: {
		iconName: "rains",
		colour: ["#ED4264", "#FFEDBC"],
		title: "FIGHT WITH RAIN",
		sub_title: "or bring an umbrella",
	},
	Snow: {
		iconName: "snow",
		colour: ["#DAE2F8", "#D6A4A4"],
		title: "WHAT A BEAUTIFUL FROZEN WATER",
		sub_title: "cold as fk",
	},
	Clear: {
		iconName: "day-sunny",
		colour: ["#EC6F66", "#F3A183"],
		title: "GET OTTA HERE",
		sub_title: "or sleep well :)",
	},
	Clouds: {
		iconName: "cloudy",
		colour: ["#49a09d", "#5f2c82"],
		title: "NOTHING'S GONNA HAPPEN",
		sub_title: "just u cannot see the sun",
	},
	Mist: {
		iconName: "center-align",
		colour: ["#757F9A", "#D7DDE8"],
		title: "THERE'S NOTHING U CAN SEE",
		sub_title: "see with ur mind",
	},
	Smoke: {
		iconName: "cloud",
		colour: ["#ECE9E6", "#ECE9E6"],
		title: "WHO SMOKED THIS SHEET",
		sub_title: "not me at least",
	},
	Haze: {
		iconName: "day-haze",
		colour: ["#134E5E", "#71B280"],
		title: "WTF IS THIS WEATHER",
		sub_title: "called haze",
	},
	Dust: {
		iconName: "center-align",
		colour: ["#02AAB0", "#00CDAC"],
		title: "UR LUNG IS UNDER ATTACK",
		sub_title: "take ur mask brah",
	},
	Fog: {
		iconName: "cloudy",
		colour: ["#348F50", "#56B4D3"],
		title: "Y THERE'S SO MANY WEATHERS WITH NO VISION",
		sub_title: "who knows",
	},
	Sand: {
		iconName: "nav-icon-grid-a",
		colour: ["#E55D87", "#5FC3E4"],
		title: "SNAD IS COMMING",
		sub_title: "maybe better than tsunami",
	},
	Ash: {
		iconName: "nav-icon-grid-a",
		colour: ["#3D7EAA", "#FFE47A"],
		title: "WHAT IS THIS SHEET",
		sub_title: "at least not lol",
	},
	Squall: {
		iconName: "cloud-gusts",
		colour: ["#603813", "#b29f94"],
		title: "WIND WIND IWND",
		sub_title: "I see typo there",
	},
	Tornado: {
		iconName: "fog",
		colour: ["#283048", "#859398"],
		title: "GL MY MAN",
		sub_title: "hope ur house is safe",
	},
};

export default function Weather({ temp, condition }) {
	return (
		<>
			<LinearGradient
				colors={weatherOptions[condition].colour}
				style={styles.container}
			>
				<StatusBar barStyle="light-content" />
				<View style={styles.top}>
					<Fontisto
						name={weatherOptions[condition].iconName}
						size={100}
						color="white"
					/>
					<Text style={styles.temp}>{temp}º</Text>
				</View>
				<View style={{ ...styles.bottom, ...styles.textContainer }}>
					<Text style={styles.title}>{weatherOptions[condition].title}</Text>
					<Text style={styles.sub_title}>
						{weatherOptions[condition].sub_title}
					</Text>
				</View>
			</LinearGradient>
		</>
	);
}

Weather.propTypes = {
	temp: PropTypes.number.isRequired,
	condition: PropTypes.oneOf([
		"Thunderstorm",
		"Drizzle",
		"Rain",
		"Snow",
		"Clear",
		"Clouds",
		"Mist",
		"Smoke",
		"Haze",
		"Dust",
		"Fog",
		"Sand",
		"Ash",
		"Squall",
		"Tornado",
	]).isRequired,
};

const styles = StyleSheet.create({
	container: {
		flex: 1,
		justifyContent: "center",
		alignItems: "center",
	},
	top: {
		flex: 1,
		justifyContent: "center",
		alignItems: "center",
	},
	temp: {
		fontSize: 32,
		color: "white",
	},
	bottom: {
		flex: 1,
		justifyContent: "center",
		alignItems: "center",
	},
	title: {
		color: "white",
		fontWeight: "300",
		fontSize: 40,
		marginBottom: 10,
		textAlign: "center",
	},
	sub_title: {
		fontSize: 20,
		fontWeight: "600",
		color: "white",
		textAlign: "center",
	},
	textContainer: {
		paddingHorizontal: 20,
		alignItems: "center",
	},
});
